<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');

class Whitelabel_model extends CI_Model {

    const default_logo = 'data:image/png;base64,';
    
    public function __construct(){
        parent::__construct();
    }
    

    public function get_partners_list(){
        
        $r =  $this->db->get('wtauth_sites')->result();
        foreach ($r as &$row){
            if ( !empty($row->logo))
                $row->img = '<img style="width: 100px; height: 60px" src="'.$row->logo.'">';
            else {
               $row->img = '<img style="width: 100px; height: 60px" src="'.self::default_logo.'">';
            }
        }
        return $r;
    }
    
    
}